package ui;

public class SubString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String letters = "abcdefghijklmabcdefghijklm";
		
		System.out.printf("substring from 20 to end: %s%n", letters.substring(20));
		
		System.out.printf("substring from 3 - 6: %s%n", letters.substring(3, 6));

	}

}
